import React, { Component } from 'react';
import Pictures from './components/pictures.js';
import Videos from './components/videos.js';


class App extends Component {
  state = {
    media:[]
  }

  componentDidMount(){
    fetch('https://api.nasa.gov/planetary/apod?api_key=UrXJyJZWfOwzULw07rfPFuiur4cEsYCmC7WaSkTZ')
    .then(res => res.json())
    .then((data) => {
      this.setState({ media: data })
    })
    .catch(console.log)
  }


  render () {
    const medType = this.state.media.media_type;
    let retRen;

    if (medType === "video")
    {
      retRen =  <Videos media={this.state.media} />;
    } else {
      retRen =  <Pictures media={this.state.media} />;
      console.log(this.state.media.media_type);
    }
      return (
        {retRen}
      );
    }
  }


export default App;
